document.addEventListener("DOMContentLoaded",()=>{const e=document.getElementById("mobileMenuToggle"),t=document.querySelector(".sidebar");e&&t&&(e.addEventListener("click",()=>{e.classList.toggle("active"),t.classList.toggle("open")}),document.addEventListener("click",n=>{!t.contains(n.target)&&!e.contains(n.target)&&(e.classList.remove("active"),t.classList.remove("open"))}))});
