document.addEventListener("DOMContentLoaded",()=>{const n=document.getElementById("logo3D");if(n){let s=!1,o=0,c=0,l=0,r=0;n.addEventListener("mousedown",t=>{s=!0,o=t.clientX,c=t.clientY,n.style.cursor="grabbing"}),document.addEventListener("mousemove",t=>{if(!s)return;const e=t.clientX-o,a=t.clientY-c;r+=e*.5,l-=a*.5,n.style.transform=`rotateX(${l}deg) rotateY(${r}deg) scale(1.1)`,o=t.clientX,c=t.clientY}),document.addEventListener("mouseup",()=>{s&&(s=!1,n.style.cursor="grab")}),n.addEventListener("touchstart",t=>{t.preventDefault();const e=t.touches[0];o=e.clientX,c=e.clientY}),n.addEventListener("touchmove",t=>{t.preventDefault();const e=t.touches[0],a=e.clientX-o,d=e.clientY-c;r+=a*.5,l-=d*.5,n.style.transform=`rotateX(${l}deg) rotateY(${r}deg) scale(1.1)`,o=e.clientX,c=e.clientY})}});
