document.addEventListener("DOMContentLoaded",function(){const i=document.querySelectorAll(".nav-menu a[data-section]");function s(){const e=["inicio","sobre-mi","proyectos","trayectoria","tools","certificados"],o=window.scrollY+window.innerHeight/2;let n="inicio";for(const t of e){const c=document.getElementById(t);if(c){const{offsetTop:a,offsetHeight:r}=c;if(o>=a&&o<a+r){n=t;break}}}i.forEach(t=>{t.getAttribute("data-section")===n?t.classList.add("active"):t.classList.remove("active")})}i.forEach(e=>{e.addEventListener("click",function(o){o.preventDefault();const n=e.getAttribute("data-section"),t=document.getElementById(n||"");t&&t.scrollIntoView({behavior:"smooth",block:"start"})})}),window.addEventListener("scroll",s),s()});
